// src/styles/GlobalStyle.js
import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  body, html {
    margin: 0;
    padding: 0;
    background-color: #121212;
    color: #e0e0e0;
    font-family: 'Roboto', sans-serif;
    line-height: 1.6;
    scroll-behavior: smooth;
  }

  #root {
    height: 100%;
  }

  .section {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    padding: 20px;
    text-align: center;
  }

  .contact-info, .button {
    background-color: #03dac6; /* Initial background color (light green) */
    color: black; /* Initial text color */
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s ease, color 0.3s ease; /* Smooth transition */
    margin-top: 20px;
    display: inline-block;
    text-decoration: none; /* Ensure it's styled as a button */
  }

  .contact-info:hover, .button:hover {
    background-color: #121212; /* Change the box to black on hover */
    color: yellow; /* Change the text to yellow on hover */
  }

  .footer {
    padding: 20px;
    text-align: center;
    border-top: 1px solid #444;
    background-color: #121212;
  }

  .footer p {
    margin: 0;
    color: white;
  }

  .footer p:hover {
    color: yellow; /* Change the footer text to yellow on hover */
