// src/components/FrontPageScene.js

import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const HandModel = () => {
  const { scene } = useGLTF('/robothands.glb');
  return <primitive object={scene} scale={[2, 2, 2]} position={[0, 0, 0]} />;
};

const Checkerboard = () => {
  return (
    <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -1.5, 0]}>
      <planeGeometry attach="geometry" args={[10, 10]} />
      <meshStandardMaterial attach="material" color="gray" />
    </mesh>
  );
};

const FrontPageScene = () => {
  return (
    <Canvas style={{ height: '100vh', background: 'black' }} shadows>
      {/* Ambient light to softly illuminate the scene */}
      <ambientLight intensity={0.1} />
      
      {/* Key spotlight to focus on the hands, creating strong highlights */}
      <spotLight 
        position={[0, 5, 5]} // Position the light in front and above the hands
        angle={0.3}           // Focused spotlight beam
        penumbra={1}          // Soft edge to the spotlight
        intensity={2}         // Strong intensity to create defined highlights
        castShadow            // Enable shadows for depth
        color="#5e7fae"       // Add a slight bluish tint to match the second image's lighting
      />
      
      {/* Fill light to add slight illumination to the opposite side */}
      <spotLight 
        position={[0, -5, 5]}  // Position light below the hands, facing upwards
        angle={0.5}            // A bit wider angle to provide fill lighting
        penumbra={1}           // Soft edge to the spotlight
        intensity={0.5}        // Lower intensity to fill shadows softly
        color="#2e3a4f"        // Darker blue fill light
        castShadow 
      />

      <Suspense fallback={null}>
        <Checkerboard />
        <HandModel />
      </Suspense>
      
      <OrbitControls />
    </Canvas>
  );
};

export default FrontPageScene;
