// src/components/FrontPageScene.js

import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const HandModel = () => {
  const { scene } = useGLTF('/robothands.glb');
  return <primitive object={scene} scale={[2, 2, 2]} position={[0, 0, 0]} />;
};

const Checkerboard = () => {
  return (
    <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -1.5, 0]}>
      <planeGeometry attach="geometry" args={[10, 10]} />
      <meshStandardMaterial attach="material" color="gray" />
    </mesh>
  );
};

const FrontPageScene = () => {
  return (
    <Canvas style={{ height: '100vh', background: 'black' }} shadows>
      {/* Ambient light for overall scene visibility */}
      <ambientLight intensity={0.3} />
      
      {/* Key spotlight to illuminate the hands from above and slightly in front */}
      <spotLight 
        position={[0, 5, 5]}    // Above and in front of the hands
        angle={0.6}             // Broader beam angle to cover more area
        penumbra={0.5}          // Softer edge
        intensity={10}         // Stronger intensity to clearly illuminate the hands
        castShadow              // Enable shadow casting
      />
      
      {/* Fill light from a different angle to add some visibility from below */}
      <spotLight 
        position={[0, -3, 3]}   // Below and in front, shining upwards
        angle={0.7}             // Wider angle for softer fill light
        penumbra={1}            // Soft edges
        intensity={1}           // Lower intensity, just to fill shadows
        castShadow 
      />

      <Suspense fallback={null}>
        <Checkerboard />
        <HandModel />
      </Suspense>
      
      <OrbitControls />
    </Canvas>
  );
};

export default FrontPageScene;
