// src/components/FrontPageScene.js
import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF, useTexture } from '@react-three/drei';

const HandModel = () => {
  console.log("HandModel component is rendering");

  const { scene } = useGLTF('/robothands.glb');
  const texture = useTexture('/textures/hand_texture.jpg');

  scene.traverse((child) => {
    if (child.isMesh) {
      console.log(`Applying texture to ${child.name}`);
      child.material.map = texture;
    }
  });

  return <primitive object={scene} scale={[2, 2, 2]} position={[0, 0, 0]} />;
};

const Checkerboard = () => {
  console.log("Checkerboard component is rendering");

  return (
    <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -1.5, 0]}>
      <planeGeometry attach="geometry" args={[10, 10]} />
      <meshStandardMaterial attach="material" color="gray" />
    </mesh>
  );
};

const FrontPageScene = () => {
  console.log("FrontPageScene component is rendering");

  return (
    <Canvas style={{ height: '100vh', background: 'black' }} shadows>
      <ambientLight intensity={0.3} />
      <spotLight position={[0, 5, 5]} angle={0.6} penumbra={0.5} intensity={75} castShadow />
      <spotLight position={[0, -3, 3]} angle={0.7} penumbra={1} intensity={1} castShadow />
      <Suspense fallback={null}>
        <Checkerboard />
        <HandModel />
      </Suspense>
      <OrbitControls />
    </Canvas>
  );
};

export default FrontPageScene;
