// src/styles/GlobalStyle.js
import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  body, html {
    margin: 0;
    padding: 0;
    background-color: #121212;
    color: #e0e0e0;
    font-family: 'Roboto', sans-serif;
    line-height: 1.6;
    scroll-behavior: smooth;
  }

  #root {
    height: 100%;
  }

  .section {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    padding: 20px;
    text-align: center;
  }

  .contact-info {
    font-size: 1.2em;
  }

  .button {
    background-color: #03dac6;
    color: black;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s ease;
    margin-top: 20px;
    display: inline-block;
  }

  .button:hover {
    background-color: #018786;
  }

  h1, h2, p {
    color: white;
  }

  h1:hover, h2:hover, p:hover {
    color: yellow;
  }

  .footer {
    padding: 20px;
    text-align: center;
    border-top: 1px solid #444;
    background-color: #121212;
  }

  .footer p {
    margin: 0;
  }

  .footer p:hover {
    color: yellow;
  }
`;

export default GlobalStyle;
