// src/components/FrontPageScene.js
import React, { useRef } from 'react'; 
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, useGLTF, useTexture } from '@react-three/drei';

const HandModel = () => {
  const { scene } = useGLTF('/robothands.glb');
  const texture = useTexture('/textures/hand_texture.jpg'); // Correct the path to match your texture

  const handRef = useRef();

  // Apply the texture to the hand model
  scene.traverse((child) => {
    if (child.isMesh) {
      child.material.map = texture;
    }
  });

  // Automatically rotate the hand model in a full 360-degree loop
  useFrame(() => {
    if (handRef.current) {
      handRef.current.rotation.y += 0.01; // Adjust the rotation speed as needed
      if (handRef.current.rotation.y >= Math.PI * 2) {
        handRef.current.rotation.y = 0; // Reset rotation after a full loop
      }
    }
  });

  return <primitive ref={handRef} object={scene} scale={[2, 2, 2]} position={[0, 0, 0]} />;
};

const Checkerboard = () => {
  return (
    <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -1.5, 0]}>
      <planeGeometry attach="geometry" args={[10, 10]} />
      <meshStandardMaterial attach="material" color="gray" />
    </mesh>
  );
};

const FrontPageScene = () => {
  return (
    <Canvas style={{ height: '100vh', background: 'black' }} shadows>
      {/* Lighting */}
      <ambientLight intensity={0.3} />
      <spotLight position={[0, 5, 5]} angle={0.6} penumbra={0.5} intensity={75} castShadow />
      <spotLight position={[0, -3, 3]} angle={0.7} penumbra={1} intensity={1} castShadow />
      
      <Checkerboard />
      <HandModel />
      
      <OrbitControls enablePan={false} enableZoom={false} enableRotate={false} />
    </Canvas>
  );
};

export default FrontPageScene;
