// src/components/FrontPageScene.js

import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const HandModel = () => {
  const { scene } = useGLTF('/robothands.glb');
  return <primitive object={scene} scale={[2, 2, 2]} position={[0, 0, 0]} />;
};

const Checkerboard = () => {
  return (
    <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -1.5, 0]}>
      <planeGeometry attach="geometry" args={[10, 10]} />
      <meshStandardMaterial attach="material" color="gray" />
    </mesh>
  );
};

const FrontPageScene = () => {
  return (
    <Canvas style={{ height: '100vh' }} shadows>
      {/* Improve the lighting setup */}
      <ambientLight intensity={0.3} />
      <spotLight position={[10, 10, 10]} angle={0.3} penumbra={1} castShadow />
      <spotLight position={[-10, 10, 10]} angle={0.3} penumbra={1} castShadow />
      <directionalLight 
        position={[5, 5, 5]} 
        intensity={1} 
        castShadow 
        shadow-mapSize-width={1024} 
        shadow-mapSize-height={1024} 
        shadow-camera-far={50} 
        shadow-camera-left={-10} 
        shadow-camera-right={10} 
        shadow-camera-top={10} 
        shadow-camera-bottom={-10} 
      />
      <Suspense fallback={null}>
        <Checkerboard />
        <HandModel />
      </Suspense>
      <OrbitControls />
    </Canvas>
  );
};

export default FrontPageScene;
