// src/styles/GlobalStyle.js
import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    font-family: 'Arial', sans-serif;
    overflow: hidden;
  }

  canvas {
    display: block;
    width: 100%;
    height: 100vh;
  }
`;

export default GlobalStyle;
