// src/components/ThreeDScene.js

import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';
import * as THREE from 'three';

const Galaxy = () => {
  const pointsRef = useRef();

  // Generate a set of random points to simulate stars
  const generateGalaxy = () => {
    const stars = new Float32Array(10000 * 3); // 10,000 points, each with x, y, z coordinates
    for (let i = 0; i < stars.length; i++) {
      stars[i] = (Math.random() - 0.5) * 10; // Random position in 3D space
    }
    return stars;
  };

  // Rotate the galaxy on each frame
  useFrame(() => {
    if (pointsRef.current) {
      pointsRef.current.rotation.y += 0.001;
    }
  });

  return (
    <Points ref={pointsRef} positions={generateGalaxy()} stride={3}>
      <PointMaterial
        transparent
        color="white"
        size={0.05}
        sizeAttenuation={true}
        depthWrite={false}
      />
    </Points>
  );
};

const ThreeDScene = () => {
  return (
    <Canvas style={{ background: 'black', height: '100vh' }}>
      <Galaxy />
      <ambientLight intensity={0.5} />
    </Canvas>
  );
};

export default ThreeDScene;
